import React, { useState } from 'react';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import {RootStore} from './Store';
import {GetPokemon} from './actions/PokemonActions';

function App() {

  const dispatch = useDispatch();
  const [ pokemonName, setPokemonName ] = useState("");

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setPokemonName(event.target.value);
  };

  const handleSubmit = () => {
    dispatch(GetPokemon(pokemonName));
  }

  const pokemonState = useSelector( (state: RootStore) => state.pokemon);
  return (
    <div className="App">
      <input type="text" onChange={handleChange}/>
      <button onClick={handleSubmit}>Search</button>
      {pokemonState.pokemon && (
        <div>
          <img src={pokemonState.pokemon.sprites.front_default} alt=""></img>
          {
            pokemonState.pokemon.abilities.map((ability,index) => {
            return <p key={index}>name: {ability.ability.name}</p>
            })
          }

          <p>Url: {pokemonState.pokemon.sprites.front_default}</p>
          {
            pokemonState.pokemon.stats.map((item,index) => {
            return <p key={index}>base_stat: {item}</p>
            })
          }

        </div>
      )}
    </div>
  );
}

export default App;
